// Selecting Elements by ID
$("#mark").css("background","#786");
$("#dis-none").css("display","none");
$("#unique").css("border","1px solid black");

// Selecting Elements by Class Name
$(".mark").css("color","crimson");
$(".mark2").css("background","skyblue");

// Selecting Elements by Name
$("h1").css("text-align","center");
$("h6").css("color","red");
$("span").css("font-size","24px");

// Selecting Elements by Attribute
$('input[type = "text"]').css('background','lightgrey');
$('input[type = "password"]').css('background','lightgrey');
$('input[type = "submit"]').css('color','blue');